import React, { useState, useEffect } from 'react';
import { getMangas } from '../services/apiService';
import './MangaList.css';
import './MangaItem.css';
import StarRating from './StarRating';

const MangaItem = ({ manga }) => {
  const [showModal, setShowModal] = useState(false);
  const [review, setReview] = useState({
    mangaId: manga.id,
    text: '',
    rating: '',
  });

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setReview({
      mangaId: manga.id,
      text: '',
      rating: '',
    });
  };

  const handleSubmitReview = () => {
    if (review.text.trim() !== '' && review.rating !== '' && review.rating >= 1 && review.rating <= 10) {
      // Отправка отзыва на сервер
      console.log('Отзыв отправлен: ', review);
      handleCloseModal();
    } else {
      alert('Пожалуйста, заполните все поля корректно.');
    }
  };

  const handleInputChange = (e) => {
    setReview({
      ...review,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="manga-item">
      <h2>{manga.title}</h2>
      <p>Описание: {manga.description}</p>
      <p>Жанр: {manga.genre.name}</p>
      <p>Автор: {manga.author.name}</p>
      <button onClick={handleOpenModal}>Написать отзыв</button>

      {showModal && (
        <div className="modal">
          <h2>Написать отзыв</h2>
          <textarea
            name="text"
            value={review.text}
            onChange={handleInputChange}
            placeholder="Введите текст отзыва..."
          />
          <input
            type="number"
            name="rating"
            value={review.rating}
            onChange={handleInputChange}
            min="1"
            max="10"
            placeholder="Оценка (от 1 до 10)"
          />
          <button onClick={handleSubmitReview}>Отправить</button>
          <button onClick={handleCloseModal}>Закрыть</button>
        </div>
      )}
    </div>
  );
};

