import React, { useState } from 'react';
import Navbar from './components/Navbar';
import MangaList from './components/MangaList';
import AuthorList from './components/AuthorList';
import GenreList from './components/GenreList';
import ReviewList from './components/ReviewList';
import './App.css';

const loginButton = {
    position: 'absolute',
    top: '10px',
    left: '10px',
    padding: '10px 20px',
    backgroundColor: '#ccc',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
};


function App() {
  const [activeTab, setActiveTab] = useState('manga');

  const renderContent = () => {
    switch (activeTab) {
      case 'manga':
        return <MangaList />;
      case 'authors':
        return <AuthorList />;
      case 'genres':
        return <GenreList />;
      case 'reviews':
        return <ReviewList />;
      default:
        return <MangaList />;
    }
  };

  return (
    <div className="App">
          <header>
              <button style={loginButton}>Admin</button>
        <h1>Manga365</h1>
      </header>
      <Navbar setActiveTab={setActiveTab} />
      <main>
        {renderContent()}
      </main>
    </div>
  );
}

export default App;
