      <p>Автор: {manga.author.name}</p>
    </div>
  );
};
*/

const MangaList = () => {
  const [mangas, setMangas] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const data = await getMangas();
      setMangas(data);
    };
    fetchData();
  }, []);

  return (
    <div className="manga-list">
      {mangas.map(manga => (
        <MangaItem key={manga.id} manga={manga} />
      ))}
    </div>
  );
};

export default MangaList;
