import React from 'react';
import './Navbar.css';
import { useLocation } from 'react-router-dom';

const Navbar = ({ setActiveTab, isAdmin }) => {
    return (
        <nav>
            <ul>
                <li onClick={() => setActiveTab('manga')}>Манга {isAdmin && <button>Добавить</button>}</li>
                <li onClick={() => setActiveTab('authors')}>Авторы {isAdmin && <button>Добавить</button>}</li>
                <li onClick={() => setActiveTab('genres')}>Жанры {isAdmin && <button>Добавить</button>}</li>
                <li onClick={() => setActiveTab('reviews')}>Отзывы {isAdmin && <button>Добавить</button>}</li>
            </ul>
        </nav>
    );
};

export default Navbar;
